import {create} from 'zustand';
interface UserState {
    user: User;
    userReturn: UserReturn;
    setUserReturn: (userReturn: UserReturn) => void;
    setUser: (user: User) => void;
    // editUser: (user: User, newUsername: string) => void;
    // removeUser: (user: User) => void;
}

