}
    const getLocalStorage = (key: string): User|UserReturn => JSON.parse(window.localStorage.getItem(key) as string);
    const setLocalStorage = (key: string, value:User|UserReturn) => window.localStorage.setItem(key, JSON.stringify(value));

    const useStore = create<UserState>((set) => ({

    user : getLocalStorage('user') || null,

    userReturn : getLocalStorage('userReturn') || null,

    setUser: (user: User) => set(() => {
        setLocalStorage('user', user)
            return {user: user}
    }),

    setUserReturn: (userReturn: UserReturn) => set(() => {
        setLocalStorage('userReturn', userReturn)
        return {userReturn: userReturn}
    }),

