import {create} from 'zustand';
interface UsersState {
    users: User[];
    setUsers: (users: Array<User>) => void;

}

const getLocalStorage = (key: string): User[] => JSON.parse(window.localStorage.getItem(key) as string);
const setLocalStorage = (key: string, value:Array<User>) => window.localStorage.setItem(key, JSON.stringify(value));

const useStore = create<UsersState>((set) => ({

    users: getLocalStorage('users') || [],
    setUsers: (users: User[]) => set(() => {
        setLocalStorage('users', users)
        return {users: users} }),

}))
export const useUsersStore = useStore;
