import axios from 'axios';
import React from "react";
import {Link} from "react-router-dom";
import {
    Alert,
    AlertTitle,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogContentText,
    DialogTitle,
    Paper, Snackbar, Stack, Table, TableBody, TableCell, TableContainer,
    TableHead, TableRow,
    TextField
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import EditIcon from "@mui/icons-material/Edit";
import CSS from 'csstype';

const card: CSS.Properties = {
    padding: "10px",
    margin: "20px",
}

interface HeadCell { id: string;
    label: string; numeric: boolean; }

const headCells: readonly HeadCell[] = [
    { id: 'ID', label: 'id', numeric: true },
    { id: 'username', label: 'Username', numeric: false }, { id: 'link', label: 'Link', numeric: false },
    { id: 'actions', label: 'Actions', numeric: false }
];
const Users = () => {
    const [users, setUsers] = React.useState <Array<User>>([])
    const [errorFlag, setErrorFlag] = React.useState(false)
    const [errorMessage, setErrorMessage] = React.useState("")
    const [username, setUsername] = React.useState("")
    const [usernameEdit, setUsernameEdit] = React.useState("")
    const [openDeleteDialog, setOpenDeleteDialog] = React.useState(false)
    const [dialogUser, setDialogUser] = React.useState<User>({username: "", user_id: -1})
    const [openEditDialog, setOpenEditDialog] = React.useState(false)
    const [snackOpen, setSnackOpen] = React.useState(false)
    const [snackMessage, setSnackMessage] = React.useState("")
    const handleSnackClose = (event?: React.SyntheticEvent | Event, reason?: string) => {
        if (reason === 'clickaway') {
            return;
        }
        setSnackOpen(false);
    }
    const handleDeleteDialogOpen = (user: User) => {
        setDialogUser(user)
        setOpenDeleteDialog(true);
    };
    const handleDeleteDialogClose = () => {
        setDialogUser({username: "", user_id: -1})
        setOpenDeleteDialog(false);
    };

    const handleEditDialogOpen = (user: User) => {
        setDialogUser(user)
        setOpenEditDialog(true);
    };
    const handleEditDialogClose = () => {
        setDialogUser({username: "", user_id: -1})
        setOpenEditDialog(false);
    };


    React.useEffect(() => {
            getUsers()
        },
        [])
    const getUsers = () => {
        axios.get('http://localhost:3000/api/users')
            .then((response) => {
                setErrorFlag(false)
                setErrorMessage("")
                setUsers(response.data)
            }, (error) => {
                setErrorFlag(true)
                setErrorMessage(error.toString())
            })
    }
    const deleteUser = (user: { user_id: any; username?: string; }) => {
        axios.delete('http://localhost:3000/api/users/' + user.user_id)
            .then((response) => {
                setUsers(users.filter(u => u.user_id !== user.user_id))
            })
        setSnackMessage("User deleted successfully")
        setSnackOpen(true)
        handleDeleteDialogClose()
    }

    const editUser = (user: { user_id: any; username?: string; }, username: string) => {

        if (username === "") {
            alert("Please enter a username!")
        } else {
            axios.put('http://localhost:3000/api/users/' + user.user_id, {"username": username})
        }
        setSnackMessage("Username changed successfully")
        setSnackOpen(true)
        handleEditDialogClose()

    }
    const updateUsernameState = (event: { target: { value: React.SetStateAction<string>; }; }) => {
        setUsername(event.target.value)
    }

    const updateUsernameEditState = (event: { target: { value: React.SetStateAction<string>; }; }) => {
        setUsernameEdit(event.target.value)
    }


    const addUser = (username: string) => {

        if (username === "") {
            alert("Please enter a username!")
        } else {
            axios.post('http://localhost:3000/api/users', {"username": username})
        }

    }


    const list_of_users = () => {
        getUsers();
        return users.map((item: User) =>
            <tr key={item.user_id}>
                <th scope="row">{item.user_id}</th>
                <td>{item.username}</td>
                <td><Link to={"/users/" + item.user_id}>Go to
                    user</Link>
                </td>
                <td>
                    <Button variant="outlined" endIcon={<DeleteIcon/>} onClick={() => {
                        handleDeleteDialogOpen(item)
                    }}>
                        Delete </Button>
                    <Dialog
                        open={openDeleteDialog}
                        onClose={handleDeleteDialogClose}
                        aria-labelledby="alert-dialog-title"
                        aria-describedby="alert-dialog-description">
                        <DialogTitle id="alert-dialog-title">
                            {"Delete User?"}
                        </DialogTitle>
                        <DialogContent>
                            <DialogContentText id="alert-dialog-description">
                                Are you sure you want to delete this user?
                            </DialogContentText>
                        </DialogContent>
                        <DialogActions>
                            <Button onClick={handleDeleteDialogClose}>Cancel</Button>
                            <Button variant="outlined" color="error" onClick={() => {
                                deleteUser(dialogUser)
                            }} autoFocus>
                                Delete
                            </Button>
                        </DialogActions>
                    </Dialog>

                    <Button variant="outlined" endIcon={<EditIcon/>} onClick={() => {
                        handleEditDialogOpen(item)
                    }}>
                        Edit </Button>
                    <Dialog
                        open={openEditDialog}
                        onClose={handleEditDialogClose}
                        aria-labelledby="alert-dialog-title"
                        aria-describedby="alert-dialog-description">
                        <DialogTitle id="alert-dialog-title">
                            {"Edit User"}
                        </DialogTitle>
                        <DialogContent>
                        </DialogContent>
                        <TextField id="outlined-basic"
                                   label="Username" variant="outlined"
                                   value={usernameEdit} onChange={updateUsernameEditState}/>
                        <DialogActions>
                            <Button onClick={handleEditDialogClose}>Cancel</Button>
                            <Button variant="outlined" color="error" onClick={() => {
                                editUser(dialogUser, usernameEdit)
                            }} autoFocus>
                                Confirm
                            </Button>
                        </DialogActions>
                    </Dialog>
                    <Snackbar
                        autoHideDuration={6000}
                        open={snackOpen}
                        onClose={handleSnackClose}
                        key={snackMessage}>
                        <Alert onClose={handleSnackClose} severity="success" sx={{
                            width: '100%'
                        }}>
                            {snackMessage}
                        </Alert>
                    </Snackbar>
                </td>
            </tr>
        )
    }


    return (
        <div>
            {errorFlag &&
                <Alert severity="error">
                    <AlertTitle>Error</AlertTitle>
                    {errorMessage}
                </Alert>}
            <Paper elevation={3} style={card}>
                <h1>Users</h1>
                <TableContainer component={Paper}>
                    <Table>
                        <TableHead>
                            <TableRow>
                                {headCells.map((headCell) => (
                                    <TableCell
                                        key={headCell.id}
                                        align={headCell.numeric ? 'right' :
                                            'left'}
                                        padding={'normal'}>
                                        {headCell.label}
                                    </TableCell>))}
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {list_of_users()}
                        </TableBody>
                    </Table>
                </TableContainer>
            </Paper>
            <Paper elevation={3} style={card}>
                <h1>Add a new user</h1>
                <Stack direction="row" spacing={2} justifyContent="center">
                    <TextField id="outlined-basic" label="Username" variant="outlined" value={username}
                               onChange={(event) => updateUsernameState(event)}/>
                    <Button variant="outlined" onClick={() => {
                        addUser(username);
                        list_of_users();
                        setUsername("")
                    }}> Submit
                    </Button>
                </Stack>
            </Paper>
        </div>)

}


export default Users;
