import {create} from 'zustand';
import FilmList from "../components/Films";
interface FilmsState {
    films: Film[];
    setFilms: (films: Film[]) => void;

    //editFilm: (film: film, newUsername: string) => void;

    removeFilm: (film: Film) => void;
}

const getLocalStorage = (key: string): Film[] => JSON.parse(window.localStorage.getItem(key) as string);
const setLocalStorage = (key: string, value:Film[]) => window.localStorage.setItem(key, JSON.stringify(value));

const useStore = create<FilmsState>((set) => ({

    films : <User> getLocalStorage('user') || null,

        userReturn : <UserReturn> getLocalStorage('userReturn') || null,

        setUser: (user: User) => set(() => {
            setLocalStorage('user', user)
            return {user: user}
        }),

        setUserReturn: (userReturn: UserReturn) => set(() => {
            setLocalStorage('userReturn', userReturn)
            return {userReturn: userReturn}
        }),

        // editUser: (user: User, newUser) => set((state) => {
        //     setLocalStorage('user', user)
        //     return {user}
        // }),

        // removeUser: (user: User) => set((state) => {
        //
        // })
    })
)
export const useFilmStore = useStore;
