import axios from 'axios';
import React from "react";
import CSS from 'csstype';
import {Paper, AlertTitle, Alert} from "@mui/material";
import FilmListObject from "./FilmListObject";
import {useFilmStore} from "../store/film";
import * as timers from "timers";
const FilmList = () => {
    const films = useFilmStore(state => state.films)
    const setFilms = useFilmStore(state => state.setFilms)
    const [errorFlag, setErrorFlag] = React.useState(false)
    const [errorMessage, setErrorMessage] = React.useState("")
    React.useEffect(() => {
        const getFilms = () => { axios.get('https://seng365.csse.canterbury.ac.nz/api/v1/films')
            .then((response) => {
                setErrorFlag(false)
                setErrorMessage("")
                setFilms(response.data.films)
            }, (error) => {
                setErrorFlag(true)
                setErrorMessage(error.toString())
            }) }
        getFilms()
    }, [setFilms])
    const film_rows = () => {
        return films
        // return films.map((film: Film) => {
        //     return <FilmListObject key={film.filmId} film={film} />;
        // });
    };

    const card: CSS.Properties = {
        padding: "10px",
        margin: "20px",
        display: "block",
        width: "fit-content" }

    return (
        <div>
            {film_rows()}
        </div>

