import axios from 'axios';
import React from "react";
import CSS from 'csstype';
import {Paper, AlertTitle, Alert} from "@mui/material";
import {useUserStore} from "../store";
import UserObject from "./UserOject";
import {useParams} from "react-router-dom";


const Users = () => {
    let { id } = useParams();
    const user = useUserStore(state => state.user)
    const setUser = useUserStore(state => state.setUser)
    const [errorFlag, setErrorFlag] = React.useState(false)
    const [errorMessage, setErrorMessage] = React.useState("")
    React.useEffect(() => {
        const getUser = () => {
            //http://localhost:4941/api/v1/users/
            axios.get('https://seng365.csse.canterbury.ac.nz/api/v1/users/'+id)
            .then((response) => {
                setErrorFlag(false)
                setErrorMessage("")
                setUser(response.data)
                console.log(response.data);
            }, (error) => {
                setErrorFlag(true)
                setErrorMessage(error.toString() + " defaulting to old users changes app may not work as expected")
            }

            )
        }
        getUser()
        }, [setUser])

    const user_profile = () =>
        <UserObject key={user.id} user={user}/>

    const card: CSS.Properties = { padding: "10px",
        margin: "20px",
        display: "block",
        width: "fit-content" }
    return (
        <Paper elevation={3} style={card}>
            <h1>User Profile</h1>
            <div style={{display:"inline-block", maxWidth:"965px",
                minWidth:"320"}}>
                {user_profile()}
            </div>
        </Paper>
    ) }
export default Users;
