import React from "react";
import axios from "axios";
import {Delete, Edit} from "@mui/icons-material";
import {useFilmStore} from "../store/film";
import {useGenresStore} from "../store/genre";
import {Button, Card, CardActions, CardContent, CardMedia, Dialog,
    DialogActions, DialogContent, DialogContentText,
    DialogTitle, IconButton, TextField, Typography} from "@mui/material"; import CSS from 'csstype';
interface IFilmProps {
    film: Film
}

interface IGenreProps {
    genre: Genre
}
const FilmListObject = (filmProps: IFilmProps, genreProps: IGenreProps) => {
    const [film] = React.useState<Film>(filmProps.film)
    const genres = useGenresStore(state => state.genres)
    const [filmImagePath, setFilmImagePath] = React.useState("")
    const setGenres = useGenresStore(state => state.setGenres)
    const [username, setUsername] = React.useState("")
    const [errorFlag, setErrorFlag] = React.useState(false)
    const [errorMessage, setErrorMessage] = React.useState("")
    const [openDeleteDialog, setOpenDeleteDialog] = React.useState(false)
    const [openEditDialog, setOpenEditDialog] = React.useState(false)
    const deleteUserFromStore = useFilmStore(state => state.removeFilm)
   // const editUserFromStore = useFilmStore(state => state.editUser)
    const handleDeleteDialogClose = () => { setOpenDeleteDialog(false);
    };
    const handleEditDialogClose = () => {
        setOpenEditDialog(false); }

    React.useEffect(() => {
        const getGenres = () => { axios.get('https://seng365.csse.canterbury.ac.nz/api/v1/films/genres')
            .then((response) => {
                setErrorFlag(false)
                setErrorMessage("")
                setGenres(response.data)
            }, (error) => {
                setErrorFlag(true)
                setErrorMessage(error.toString())
            }) }
        getGenres()
    }, [setGenres])


    // const deleteFilm = () => { axios.delete('http://localhost:3000/api/users/' + user.user_id)
    //     .then(() => { deleteUserFromStore(user)
    //     }) }
    // const editUser = () => { axios.put('http://localhost:3000/api/users/'+user.user_id,
    //     {"username": username}) .then(() => {
    //     editUserFromStore(user, username) })
    // }
    const userCardStyles: CSS.Properties = {
        display: "inline-block",
        height: "500px",
        width: "350px",
        margin: "10px",
        padding: "5px" }

    const getFilmGenre = (film: Film) => {
        return genres.map((genre: Genre) => {
           if (genre.genreId === film.genreId) {
               return genre.name
           }
        });
    }

    const getImagePath = (film: Film) => {
        const path = 'https://seng365.csse.canterbury.ac.nz/api/v1/films/' + film.filmId + '/image'
        axios.get(path)
            .then((response) => {
                setFilmImagePath(path)
            }, (error) => {
                setErrorFlag(true)
                setErrorMessage(error.toString())
                setFilmImagePath('../../storage/Film/defaultFilm')
            })
    }

    function handleImageLoad(event: React.SyntheticEvent<HTMLImageElement>) {
        const imageElement = event.target as HTMLImageElement;
        console.log('Image loaded:', imageElement.src);
    }


    return (
        <Card sx={userCardStyles}>
            <CardMedia
                component="img"
                sx={{ objectFit: "contain" }}
                image={filmImagePath}
                alt="Movie poster"
            />
            <CardContent>
            <Typography variant="h4">
                {film.title}
                {film.filmId}
            </Typography>
            <Typography variant="body2" color="text.secondary">
                {film.ageRating}
            </Typography>
            <Typography variant="body2" color="text.secondary">
                {film.releaseDate}
                {getFilmGenre(film)}
            </Typography>
            <Typography variant="body2" color="text.secondary">
               {getFilmGenre(film)}
            </Typography>
            </CardContent>
            <CardActions>
                <IconButton onClick={() => {setOpenEditDialog(true)}}>
                    <Edit/>
                </IconButton>
                <IconButton onClick={() => {setOpenDeleteDialog(true)}}>
                    <Delete/>
                </IconButton>
            </CardActions>
        </Card> )
}

    export default FilmListObject
